import React, { useState, useCallback } from 'react';
import MilitaryScore from './components/MilitaryScore';
import TreasuryScore from './components/TreasuryScore';
import WondersScore from './components/WondersScore';
import CivilianScore from './components/CivilianScore';
import ScienceScore from './components/ScienceScore';
import CommercialScore from './components/CommercialScore';
import GuildsScore from './components/GuildsScore';
import FinalScore from './components/FinalScore';

function App() {
  const [scores, setScores] = useState({
    military: 0,
    treasury: 0,
    wonders: 0,
    civilian: 0,
    science: 0,
    commercial: 0,
    guilds: 0
  });

  const [coins, setCoins] = useState(0);

  const updateScore = useCallback((category, score) => {
    setScores(prev => ({
      ...prev,
      [category]: score
    }));
  }, []);

  const totalScore = Object.values(scores).reduce((sum, score) => sum + score, 0);

  return (
    <div className="container">
      <div className="header">
        <h1>Calculadora de Pontos Seven Wonders</h1>
        <p>Calcule seus pontos de vit√≥ria para o jogo Seven Wonders</p>
      </div>

      <div className="calculator-grid">
        <MilitaryScore onScoreChange={(score) => updateScore('military', score)} />
        <TreasuryScore 
          onScoreChange={(score) => updateScore('treasury', score)} 
          onCoinsChange={setCoins}
        />
        <WondersScore onScoreChange={(score) => updateScore('wonders', score)} />
        <CivilianScore onScoreChange={(score) => updateScore('civilian', score)} />
        <ScienceScore onScoreChange={(score) => updateScore('science', score)} />
        <CommercialScore onScoreChange={(score) => updateScore('commercial', score)} />
        <GuildsScore onScoreChange={(score) => updateScore('guilds', score)} />
      </div>

      <FinalScore scores={scores} totalScore={totalScore} coins={coins} />
    </div>
  );
}

export default App;
