import React, { useState, useEffect } from 'react';

function GuildsScore({ onScoreChange }) {
  const [guildPoints, setGuildPoints] = useState(0);

  useEffect(() => {
    onScoreChange(guildPoints);
  }, [guildPoints, onScoreChange]);

  return (
    <div className="score-card">
      <h3>üèõÔ∏è Guilds (Purple Cards)</h3>

      <div className="input-group">
        <label>Total Guild Points:</label>
        <input
          type="number"
          min="0"
          value={guildPoints}
          onChange={(e) => setGuildPoints(parseInt(e.target.value) || 0)}
        />
      </div>

      <div className="score-display">
        <div className="score">{guildPoints} points</div>
        <small>Calculate based on your guild cards' specific rules</small>
      </div>
    </div>
  );
}

export default GuildsScore;
